mapper.select:
SELECT sa.id AS applicationId, a.id AS applicantId, a.full_name AS applicantFullName, sa.application_date AS applicationDate, sa.amount_requested AS amountRequested, sa.status AS status FROM subsidy_applications sa JOIN applicants a ON a.id = sa.applicant_id WHERE sa.deleted_at IS NULL AND a.deleted_at IS NULL
AND sa.status = #{status}
AND sa.application_date >= #{from}
AND sa.application_date <= #{to}
AND a.full_name LIKE CONCAT('%', #{q}, '%')
ORDER BY sa.id DESC
